import jetbrains.buildServer.configs.kotlin.v2019_2.ui.*

/*
This patch script was generated by TeamCity on settings change in UI.
To apply the patch, change the buildType with id = 'TestOnLinux'
accordingly, and delete the patch script.
*/
changeBuildType(RelativeId("TestOnLinux")) {
    features {
        val feature1 = find<CommitStatusPublisher> {
            commitStatusPublisher {
                publisher = github {
                    githubUrl = "https://api.github.com"
                    authType = personalToken {
                        token = "credentialsJSON:70b760a0-25e3-406b-9ed2-d73026115dc1"
                    }
                }
            }
        }
        feature1.apply {
            publisher = github {
                githubUrl = "https://api.github.com"
                authType = personalToken {
                    token = "credentialsJSON:7416c240-5c67-48ed-97a3-f5fe49d0e744"
                }
            }
        }
    }

    requirements {
        remove {
            exists("system.Octopus.Docker")
        }
        add {
            exists("system.Octopus.Docker", "RQ_1")
        }
        add {
            equals("system.Octopus.OS", "Ubuntu")
        }
    }

    expectDisabledSettings()
    updateDisabledSettings("RQ_1")
}
